<style>
.munchico-category-banner-header{position:relative;padding:var(--space-2xl) var(--space-xl);margin-bottom:var(--space-2xl);overflow:hidden;border-radius:var(--radius-xl);background:linear-gradient(135deg,var(--primary-color) 0%,var(--primary-dark) 100%);box-shadow:0 10px 40px rgba(91,77,63,0.15)}
.munchico-banner-background{position:absolute;inset:0;overflow:hidden;pointer-events:none}
.munchico-banner-pattern{position:absolute;inset:0;background-image:radial-gradient(circle at 20% 30%,rgba(229,164,67,0.1) 0%,transparent 50%),radial-gradient(circle at 80% 70%,rgba(78,148,79,0.1) 0%,transparent 50%);animation:munchico-patternFloat 20s ease-in-out infinite}
.munchico-banner-glow{position:absolute;inset:-50%;background:radial-gradient(circle,rgba(229,164,67,0.15) 0%,transparent 70%);animation:munchico-glowPulse 8s ease-in-out infinite}
.munchico-banner-content{position:relative;text-align:center;max-width:800px;margin:0 auto;z-index:2}
.munchico-banner-badge{display:inline-flex;align-items:center;gap:var(--space-xs);padding:var(--space-xs) var(--space-md);background:rgba(229,164,67,0.15);border:1px solid rgba(229,164,67,0.3);border-radius:var(--radius-full);color:var(--accent-color);font-size:var(--font-size-sm);font-weight:600;margin-bottom:var(--space-lg);backdrop-filter:blur(10px);animation:munchico-badgeBounce 3s ease-in-out infinite}
.munchico-banner-badge svg{animation:munchico-starRotate 4s linear infinite}
.munchico-banner-title{font-size:clamp(2rem,5vw,3.5rem);font-weight:800;color:var(--white);margin-bottom:var(--space-md);line-height:1.2;text-shadow:0 2px 20px rgba(0,0,0,0.2)}
.munchico-highlight-text{background:linear-gradient(135deg,var(--accent-color),#f4c563);-webkit-background-clip:text;-webkit-text-fill-color:transparent;background-clip:text;position:relative;display:inline-block}
.munchico-highlight-text::after{content:'';position:absolute;bottom:-5px;left:0;right:0;height:3px;background:linear-gradient(90deg,transparent,var(--accent-color),transparent);animation:munchico-underlineGlow 2s ease-in-out infinite}
.munchico-banner-subtitle{font-size:var(--font-size-lg);color:rgba(255,255,255,0.85);margin-bottom:var(--space-xl);font-weight:400;letter-spacing:0.5px}
.munchico-banner-stats{display:flex;justify-content:center;align-items:center;gap:var(--space-xl);flex-wrap:wrap;margin-top:var(--space-xl)}
.munchico-stat-item{display:flex;flex-direction:column;align-items:center;gap:var(--space-xs)}
.munchico-stat-number{font-size:clamp(1.5rem,3vw,2rem);font-weight:700;color:var(--accent-color);line-height:1}
.munchico-stat-label{font-size:var(--font-size-sm);color:rgba(255,255,255,0.7);font-weight:500;text-transform:uppercase;letter-spacing:1px}
.munchico-stat-divider{width:1px;height:40px;background:linear-gradient(to bottom,transparent,rgba(255,255,255,0.3),transparent)}
.munchico-banner-decoration{position:absolute;opacity:0.15;pointer-events:none;color:var(--accent-color)}
.munchico-banner-decoration.munchico-left{top:-30px;left:-30px;animation:munchico-decorationFloat 15s ease-in-out infinite}
.munchico-banner-decoration.munchico-right{bottom:-30px;right:-30px;animation:munchico-decorationFloat 15s ease-in-out infinite reverse}
@keyframes munchico-patternFloat{0%,100%{transform:translate(0,0) rotate(0deg)}50%{transform:translate(20px,20px) rotate(5deg)}}
@keyframes munchico-glowPulse{0%,100%{opacity:0.5;transform:scale(1)}50%{opacity:0.8;transform:scale(1.1)}}
@keyframes munchico-badgeBounce{0%,100%{transform:translateY(0)}50%{transform:translateY(-5px)}}
@keyframes munchico-starRotate{from{transform:rotate(0deg)}to{transform:rotate(360deg)}}
@keyframes munchico-underlineGlow{0%,100%{opacity:0.5}50%{opacity:1}}
@keyframes munchico-decorationFloat{0%,100%{transform:translate(0,0) rotate(0deg)}33%{transform:translate(10px,-10px) rotate(5deg)}66%{transform:translate(-10px,10px) rotate(-5deg)}}
.munchico-shop-by-category{padding:var(--space-2xl) 0;background-color:var(--gray-50);position:relative}
.munchico-category-slider-container{position:relative;overflow:hidden;margin:0 auto;max-width:var(--container-width)}
.munchico-category-slider-track{overflow:hidden;padding:var(--space-md) var(--space-xl)}
.munchico-category-slider{display:flex;gap:var(--space-lg);width:max-content;will-change:transform}
.munchico-category-card{flex:0 0 300px;background:var(--white);border-radius:var(--radius-xl);box-shadow:var(--shadow-md);overflow:hidden;transition:all var(--transition-base);cursor:pointer;position:relative;user-select:none}
.munchico-category-card:hover{transform:translateY(-8px);box-shadow:var(--shadow-lg)}
.munchico-category-image{position:relative;height:220px;overflow:hidden}
.munchico-category-img{width:100%;height:100%;object-fit:cover;transition:transform var(--transition-slow);pointer-events:none}
.munchico-category-card:hover .munchico-category-img{transform:scale(1.05)}
.munchico-slider-nav-btn{position:absolute;top:50%;transform:translateY(-50%);width:48px;height:48px;border-radius:var(--radius-full);background:var(--primary-color);border:2px solid var(--gray-200);color:var(--primary-color);display:flex;align-items:center;justify-content:center;cursor:pointer;transition:all var(--transition-base);z-index:10;box-shadow:var(--shadow-md)}
.munchico-slider-nav-btn:hover:not(:disabled){background:var(--primary-color);color:var(--white);border-color:var(--primary-color);transform:translateY(-50%) scale(1.1)}
.munchico-slider-nav-btn:active:not(:disabled){transform:translateY(-50%) scale(0.95)}
.munchico-slider-nav-btn:disabled{cursor:not-allowed;pointer-events:none}
.munchico-prev-btn{left:var(--space-md)}
.munchico-next-btn{right:var(--space-md)}
.munchico-category-badge{position:absolute;top:var(--space-sm);padding:var(--space-xs) var(--space-sm);border-radius:var(--radius-full);font-size:var(--font-size-xs);font-weight:700;text-transform:uppercase;letter-spacing:0.5px;z-index:2;pointer-events:none}
.munchico-discount-badge{right:var(--space-sm);background:var(--accent-color);color:var(--white)}
.munchico-hot-badge{left:var(--space-sm);background:var(--danger-color);color:var(--white)}
.munchico-new-badge{left:var(--space-sm);background:var(--secondary-color);color:var(--white)}
.munchico-category-image .munchico-category-badge:nth-child(3){top:calc(var(--space-sm) + 30px)}
.munchico-category-image .munchico-category-badge:nth-child(4){top:calc(var(--space-sm) + 60px)}
.munchico-category-content{padding:var(--space-lg);text-align:center;position:relative}
.munchico-category-name{font-size:var(--font-size-lg);font-weight:700;color:var(--gray-900);margin-bottom:var(--space-xs);text-transform:uppercase}
.munchico-category-count{font-size:var(--font-size-sm);color:var(--gray-600);font-weight:500;margin-bottom:var(--space-sm)}
.munchico-quantity-badge{display:inline-block;padding:var(--space-xs) var(--space-sm);background:var(--gray-100);color:var(--gray-700);border-radius:var(--radius-full);font-size:var(--font-size-xs);font-weight:600;text-transform:uppercase}
.munchico-view-all-btn-container{margin-top:var(--space-lg);display:flex;justify-content:center;align-items:center}
.munchico-view-all-categories-btn{position:relative;overflow:hidden;border:0!important;text-decoration:none!important;transition:all 0.3s cubic-bezier(0.4,0,0.2,1);background-color:var(--primary-color);color:var(--white);padding:0.75rem 1.75rem}
.munchico-view-all-categories-btn .munchico-arrow-icon{transition:transform 0.3s cubic-bezier(0.4,0,0.2,1)}
.munchico-view-all-categories-btn:hover{background-color:var(--gray-50);border-style:solid;border-color:var(--primary-color);color:var(--primary-color)}
.munchico-view-all-categories-btn:hover .munchico-arrow-icon{transform:translateX(4px);animation:munchico-arrowSlide 0.6s ease-in-out}
@keyframes munchico-arrowSlide{0%,100%{transform:translateX(0)}50%{transform:translateX(8px)}}
.munchico-view-all-categories-btn::before{content:'';position:absolute;top:0;left:-100%;width:100%;height:100%;background:linear-gradient(90deg,transparent,rgba(255,255,255,0.2),transparent);transition:left 0.5s ease}
.munchico-view-all-categories-btn:hover::before{left:100%}
.munchico-view-all-categories-btn::after{content:'';position:absolute;top:50%;left:50%;width:0;height:0;border-radius:50%;background:rgba(91,77,63,0.1);transform:translate(-50%,-50%);transition:width 0.4s ease,height 0.4s ease}
.munchico-view-all-categories-btn:active::after{width:300px;height:300px}
@media (max-width:768px){
.munchico-category-banner-header{padding:var(--space-xl) var(--space-md);margin-bottom:var(--space-xl)}
.munchico-banner-title{font-size:2rem}
