<script>
document.addEventListener('DOMContentLoaded',function(){const slider=document.querySelector('.munchico-category-slider');const track=document.querySelector('.munchico-category-slider-track');const cards=document.querySelectorAll('.munchico-category-card');const prevBtn=document.querySelector('.munchico-prev-btn');const nextBtn=document.querySelector('.munchico-next-btn');if(!slider||!track||!cards.length){console.error('Category slider elements not found');return}let currentIndex=0;let autoSlideInterval;let isTransitioning=false;function getSliderDimensions(){const cardStyle=getComputedStyle(cards[0]);const cardWidth=cards[0].offsetWidth;const gap=parseInt(getComputedStyle(slider).gap)||0;const trackWidth=track.offsetWidth;const visibleCards=Math.floor(trackWidth/(cardWidth+gap));const maxIndex=Math.max(0,cards.length-visibleCards);return{cardWidth,gap,visibleCards,maxIndex,slideWidth:cardWidth+gap}}let dimensions=getSliderDimensions();function updateSlider(smooth=true){const offset=currentIndex*dimensions.slideWidth;if(smooth){slider.style.transition='transform 0.5s ease-in-out'}else{slider.style.transition='none'}slider.style.transform=`translateX(-${offset}px)`;updateButtonStates()}function updateButtonStates(){if(prevBtn&&nextBtn){prevBtn.disabled=currentIndex===0;prevBtn.style.opacity=currentIndex===0?'0.5':'1';nextBtn.disabled=currentIndex>=dimensions.maxIndex;nextBtn.style.opacity=currentIndex>=dimensions.maxIndex?'0.5':'1'}}function nextSlide(){if(isTransitioning)return;isTransitioning=true;if(currentIndex<dimensions.maxIndex){currentIndex++}else{currentIndex=0}updateSlider();setTimeout(()=>{isTransitioning=false},500)}function prevSlide(){if(isTransitioning)return;isTransitioning=true;if(currentIndex>0){currentIndex--}else{currentIndex=dimensions.maxIndex}updateSlider();setTimeout(()=>{isTransitioning=false},500)}function startAutoSlide(){stopAutoSlide();autoSlideInterval=setInterval(()=>{nextSlide()},4000)}function stopAutoSlide(){if(autoSlideInterval){clearInterval(autoSlideInterval);autoSlideInterval=null}}if(prevBtn){prevBtn.addEventListener('click',()=>{stopAutoSlide();prevSlide();setTimeout(startAutoSlide,5000)})}if(nextBtn){nextBtn.addEventListener('click',()=>{stopAutoSlide();nextSlide();setTimeout(startAutoSlide,5000)})}slider.addEventListener('mouseenter',stopAutoSlide);slider.addEventListener('mouseleave',startAutoSlide);cards.forEach(card=>{card.addEventListener('click',function(){const categoryName=this.querySelector('.munchico-category-name').textContent;console.log(`Navigating to ${categoryName} category`)})});let touchStartX=0;let touchEndX=0;let isSwiping=false;slider.addEventListener('touchstart',(e)=>{touchStartX=e.changedTouches[0].screenX;isSwiping=true;stopAutoSlide()},{passive:true});slider.addEventListener('touchmove',(e)=>{if(!isSwiping)return;touchEndX=e.changedTouches[0].screenX},{passive:true});slider.addEventListener('touchend',()=>{if(!isSwiping)return;handleSwipe();isSwiping=false;setTimeout(startAutoSlide,3000)});function handleSwipe(){const swipeThreshold=50;const diff=touchStartX-touchEndX;if(Math.abs(diff)>swipeThreshold){if(diff>0){nextSlide()}else{prevSlide()}}}let resizeTimeout;window.addEventListener('resize',()=>{clearTimeout(resizeTimeout);resizeTimeout=setTimeout(()=>{dimensions=getSliderDimensions();if(currentIndex>dimensions.maxIndex){currentIndex=dimensions.maxIndex}updateSlider(false)},250)});updateSlider(false);startAutoSlide();console.log('Category slider loaded successfully');console.log('Slider info:',{totalCards:cards.length,visibleCards:dimensions.visibleCards,maxIndex:dimensions.maxIndex})});
</script>