<style>
.munchico-featured-products,.munchico-dried-fruits{background:var(--gray-50)}
.munchico-section-title{display:flex;align-items:center;justify-content:center;gap:var(--space-lg);font-size:var(--font-size-2xl);font-weight:700;color:var(--gray-900);margin-bottom:var(--space-2xl);text-transform:capitalize}
.munchico-title-line{color:var(--secondary-color);font-weight:400}
.munchico-product-slider-wrapper{position:relative;padding:0 var(--space-3xl)}
.munchico-product-slider{display:flex;gap:var(--space-xl);overflow-x:auto;scroll-behavior:smooth;scrollbar-width:none;padding:var(--space-md) 0}
.munchico-product-slider::-webkit-scrollbar{display:none}
.munchico-slider-nav-btn{position:absolute;top:50%;transform:translateY(-50%);background:var(--primary-color);border:2px solid var(--gray-300);width:45px;height:45px;border-radius:50%;display:flex;align-items:center;justify-content:center;cursor:pointer;z-index:5;transition:all var(--transition-base);box-shadow:var(--shadow-md)}
.munchico-slider-nav-btn:hover{background:var(--secondary-color);border-color:var(--secondary-color);color:var(--white);box-shadow:var(--shadow-lg)}
.munchico-slider-nav-btn.munchico-prev-btn{left:0}
.munchico-slider-nav-btn.munchico-next-btn{right:0}
.munchico-product-slider-card{min-width:300px;flex-shrink:0;background:var(--white);border-radius:var(--radius-lg);overflow:hidden;box-shadow:var(--shadow-md);transition:transform var(--transition-base),box-shadow var(--transition-base)}
.munchico-product-slider-card:hover{transform:translateY(-8px);box-shadow:var(--shadow-xl)}
.munchico-product-slider-image{position:relative;background:linear-gradient(135deg,#2C5F2D 0%,#1B3A1C 100%);padding:var(--space-2xl);display:flex;align-items:center;justify-content:center;min-height:300px}
.munchico-product-slider-image img{width:100%;max-width:180px;height:auto;border-radius:var(--radius-lg);transition:transform var(--transition-base)}
.munchico-product-slider-card:hover .munchico-product-slider-image img{transform:scale(1.05)}
.munchico-discount-badge,.munchico-quality-badge,.munchico-brand-small-badge{position:absolute;padding:6px 12px;border-radius:6px;font-size:12px;font-weight:600;text-transform:uppercase;z-index:2}
.munchico-discount-badge{top:15px;right:15px;background:#dc2626;color:var(--white)}
.munchico-quality-badge{top:15px;left:15px;background:rgba(255,255,255,0.95);color:var(--primary-color);font-size:10px;padding:4px 8px}
.munchico-brand-small-badge{top:50px;left:15px;background:var(--secondary-color);color:var(--white);font-size:10px;padding:4px 8px}
.munchico-product-actions{position:absolute;right:15px;top:50%;transform:translateY(-50%);display:flex;flex-direction:column;gap:10px;opacity:0;transition:opacity var(--transition-base)}
.munchico-product-slider-card:hover .munchico-product-actions{opacity:1}
.munchico-action-btn{width:40px;height:40px;border-radius:50%;background:var(--white);border:none;display:flex;align-items:center;justify-content:center;cursor:pointer;transition:all 0.3s ease;box-shadow:0 2px 8px rgba(0,0,0,0.1)}
.munchico-action-btn:hover{background:var(--secondary-color);color:var(--white);transform:scale(1.1)}
.munchico-action-btn svg{width:20px;height:20px}
.munchico-order-now-btn{position:absolute;bottom:15px;left:50%;transform:translateX(-50%);background:var(--white);color:var(--primary-color);border:none;padding:10px 30px;border-radius:25px;font-weight:600;font-size:14px;cursor:pointer;transition:all 0.3s ease;opacity:0}
.munchico-product-slider-card:hover .munchico-order-now-btn{opacity:1}
.munchico-order-now-btn:hover{background:var(--secondary-color);color:var(--white);transform:translateX(-50%) scale(1.05)}
.munchico-product-slider-content{padding:var(--space-xl)}
.munchico-product-slider-title{font-size:16px;font-weight:600;color:var(--gray-900);margin-bottom:var(--space-md);white-space:nowrap;overflow:hidden;text-overflow:ellipsis}
.munchico-product-rating{display:flex;align-items:center;gap:var(--space-md);margin-bottom:var(--space-md)}
.munchico-stars{color:#fbbf24;font-size:16px;letter-spacing:2px}
.munchico-review-count{font-size:13px;color:#6b7280}
.munchico-product-price{display:flex;align-items:center;gap:var(--space-md)}
.munchico-price-current{font-size:18px;font-weight:700;color:var(--primary-color)}
.munchico-price-old{font-size:15px;color:#9ca3af;text-decoration:line-through}
@media (max-width:768px){
.munchico-product-slider-wrapper{padding:0 var(--space-xl)}
.munchico-slider-nav-btn{width:35px;height:35px}
.munchico-product-slider-card{min-width:250px}
.munchico-section-title{font-size:1.5rem}
}
@media (max-width:480px){
.munchico-product-slider-wrapper{padding:0 var(--space-md)}
.munchico-product-slider-card{min-width:220px}
.munchico-product-slider-image{min-height:250px;padding:var(--space-xl)}
.munchico-product-slider-image img{max-width:150px}
}
</style>

<script>
document.addEventListener('DOMContentLoaded',function(){initializeMunchicoSliders()});function initializeMunchicoSliders(){const sections=document.querySelectorAll('.munchico-featured-products,.munchico-dried-fruits');sections.forEach(section=>{const slider=section.querySelector('.munchico-product-slider');const prevBtn=section.querySelector('.munchico-prev-btn');const nextBtn=section.querySelector('.munchico-next-btn');if(!slider||!prevBtn||!nextBtn)return;const getScrollAmount=()=>{const card=slider.querySelector('.munchico-product-slider-card');if(!card)return 0;const cardWidth=card.offsetWidth;const gap=24;return cardWidth+gap};nextBtn.addEventListener('click',()=>{const scrollAmount=getScrollAmount();slider.scrollBy({left:scrollAmount,behavior:'smooth'})});prevBtn.addEventListener('click',()=>{const scrollAmount=getScrollAmount();slider.scrollBy({left:-scrollAmount,behavior:'smooth'})});const updateNavButtons=()=>{const maxScroll=slider.scrollWidth-slider.clientWidth;if(slider.scrollLeft<=0){prevBtn.style.opacity='0.5';prevBtn.style.pointerEvents='none'}else{prevBtn.style.opacity='1';prevBtn.style.pointerEvents='auto'}if(slider.scrollLeft>=maxScroll-1){nextBtn.style.opacity='0.5';nextBtn.style.pointerEvents='none'}else{nextBtn.style.opacity='1';nextBtn.style.pointerEvents='auto'}};slider.addEventListener('scroll',updateNavButtons);updateNavButtons();window.addEventListener('resize',updateNavButtons)})}function enableMunchicoAutoScroll(sectionSelector,interval=3000){const section=document.querySelector(sectionSelector);if(!section)return;const slider=section.querySelector('.munchico-product-slider');const nextBtn=section.querySelector('.munchico-next-btn');if(!slider||!nextBtn)return;let autoScrollInterval=setInterval(()=>{const maxScroll=slider.scrollWidth-slider.clientWidth;if(slider.scrollLeft>=maxScroll-1){slider.scrollTo({left:0,behavior:'smooth'})}else{nextBtn.click()}},interval);slider.addEventListener('mouseenter',()=>{clearInterval(autoScrollInterval)});slider.addEventListener('mouseleave',()=>{autoScrollInterval=setInterval(()=>{const maxScroll=slider.scrollWidth-slider.clientWidth;if(slider.scrollLeft>=maxScroll-1){slider.scrollTo({left:0,behavior:'smooth'})}else{nextBtn.click()}},interval)})}function enableMunchicoTouchSwipe(){const sliders=document.querySelectorAll('.munchico-product-slider');sliders.forEach(slider=>{let isDown=false;let startX;let scrollLeft;slider.addEventListener('mousedown',(e)=>{isDown=true;slider.style.cursor='grabbing';startX=e.pageX-slider.offsetLeft;scrollLeft=slider.scrollLeft});slider.addEventListener('mouseleave',()=>{isDown=false;slider.style.cursor='grab'});slider.addEventListener('mouseup',()=>{isDown=false;slider.style.cursor='grab'});slider.addEventListener('mousemove',(e)=>{if(!isDown)return;e.preventDefault();const x=e.pageX-slider.offsetLeft;const walk=(x-startX)*2;slider.scrollLeft=scrollLeft-walk})})}enableMunchicoAutoScroll('.munchico-featured-products',4000);enableMunchicoAutoScroll('.munchico-dried-fruits',4000);enableMunchicoTouchSwipe();
</script>